import React, { useState } from "react";//Added today
import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View, Image, TouchableOpacity, Alert } from 'react-native';
import AnimatedSplash from "react-native-animated-splash-screen";//Added today

const Separator = () => <View style={styles.separator} />;

export default function App() {
  const [loading, setLoading] = useState(false);//Added today

  setTimeout(() => {
    setLoading(true);
  }, 3000);//Added today

  return (
   //Added today 
   <AnimatedSplash
      translucent={true}
      isLoaded={loading}
      logoImage={require("./assets/splash.png")}
      backgroundColor={"#262626"}
      logoHeight={150}
      logoWidth={150}
    >

    <View style={styles.container}>
      <Image style={styles.logo} source={require('./assets/HomePageLogo.png')}/>

      <TouchableOpacity style={styles.fpbuttons}
        onPress={() => Alert.alert('Login pressed')}>
          <Text style={styles.fpbuttontext}>Login</Text>
        </TouchableOpacity>
      <Separator/>
      <TouchableOpacity
        style={styles.fpbuttons}
        onPress={() => Alert.alert('Register pressed')}>
          <Text style={styles.fpbuttontext}>Register</Text>
        </TouchableOpacity>

      <StatusBar style="auto" />
    </View>
     </AnimatedSplash>
  );
}


const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  }, 
  logo:{
    width: 300,
    height: 300,
    resizeMode: 'contain'
  },
  fpbuttons: {
    alignItems: 'center',
    backgroundColor: '#e56e29',
    padding: 10,
    width:'60%'
  }, 
  fpbuttontext: {
    fontSize: 20,
    fontWeight: 'bold',
    color: '#fff',
  },
  separator: {
    marginVertical: 8,
    borderBottomColor: '#737373',
    borderBottomWidth: StyleSheet.hairlineWidth,
  },
});
